mapper.select:

select 

from KY_JYHTQ_CY
where S_BATCH_NUMBER = #{sBatchNumber,jdbcType=VARCHAR}
  and S_DEVICE_CODE = #{sDeviceCode,jdbcType=VARCHAR}
  and N_TIMES = #{nTimes,jdbcType=DECIMAL}

mapper.delete:

delete from KY_JYHTQ_CY
where S_BATCH_NUMBER = #{sBatchNumber,jdbcType=VARCHAR}
  and S_DEVICE_CODE = #{sDeviceCode,jdbcType=VARCHAR}
  and N_TIMES = #{nTimes,jdbcType=DECIMAL}
